INSERT INTO `movies` (`id`, `age_restriction`, `available`, `description`, `dubbing`, `duration`, `genre`, `subtitles`, `three_dimensional`, `title`, `two_dimensional`, `voice_over`) VALUES (9, 13, '1', 'Acclaimed auteur Christopher Nolan directs this World War II thriller about the evacuation of Allied troops from the French city of Dunkirk before Nazi forces can take hold. Tom Hardy, Kenneth Branagh and Mark Rylance co-star, with longtime Nolan collaborator Hans Zimmer providing the score.', '0', 107, 'Action & Adventure, Drama', '1', '1', 'Dunkirk', '1', '0');
INSERT INTO `movies` (`id`, `age_restriction`, `available`, `description`, `dubbing`, `duration`, `genre`, `subtitles`, `three_dimensional`, `title`, `two_dimensional`, `voice_over`) VALUES (8, 18, '1', 'A high-stakes, global action-thriller that takes place in the city of Berlin, on the eve of the Walls collapse and the shifting of superpower alliances. Charlize Theron ("Mad Max: Fury Road") portrays Lorraine Broughton, a top-level spy for MI6, who is dispatched to Berlin to take down a ruthless espionage ring that has just killed an undercover agent for reasons unknown. She is ordered to cooperate with Berlin station chief David Percival (James McAvoy of "X-Men"), and the two form an uneasy alliance, unleashing their full arsenal of skills in pursuing a threat that jeopardizes the Wests entire intelligence operation.', '0', 115, 'Action & Adventure, Drama, Mystery & Suspense', '1', '0', 'Atomic Blonde', '1', '1');
